public class ExemploSoma {
   public int soma(int a, int b) {
      System.out.println("a = " + a);
      System.out.println("b = " + b);

      int soma = a + b;

      System.out.println("a + b = " + soma);

      return soma;
   }

   public static void main(String[] args) {
      System.out.println(new ExemploSoma().soma(2, 2));
   }
}

public class ExemploSomaRemota {
   /**
    * @Remotable
    */
   public int soma(int a, int b) {
      System.out.println("a = " + a);
      System.out.println("b = " + b);

      int soma = a + b;

      System.out.println("a + b = " + soma);

      return soma;
   }

   public static void main(String[] args) {
      System.out.println(new ExemploSomaRemota().soma(2, 2));
   }
}
